const m=document.getElementById("menutoggler");const t=document.getElementById("themetoggler");var ms=false;var ct="light";t.addEventListener("click",()=>{toggleTheme()});m.addEventListener("click",()=>{toggleMobileMenu()});window.onload=()=>{var st=localStorage.getItem("theme");if(ct!==st){toggleTheme()}};function toggleTheme(){document.body.classList.toggle("darkmode");ct==="light"?ct="dark":ct="light";localStorage.setItem("theme",ct)};function toggleMobileMenu(){if(ms===false){document.querySelector("nav").style.opacity="1";document.querySelector("nav").style.visibility="visible";ms=true}else{document.querySelector("nav").style.opacity="0";document.querySelector("nav").style.visibility="hidden";ms=false}}